UPDATE `script_texts` SET `content_loc8` = 'Предупреждение! Форма жизни не распознана. Обработка архива прекращена.  Последующее вмешательство приведет к нацеленному противодействию.' WHERE `entry` = -1599027;
UPDATE `script_texts` SET `content_loc8` = NULL,`broadcast_text_id` = 30553 WHERE `entry` = -1599028;
UPDATE `script_texts` SET `content_loc8` = 'Обнаружено вторжение в систему. Приоритетность работ по анализу исторических архивов понижена. Ответные меры инициированы.' WHERE `entry` = -1599029;
UPDATE `script_texts` SET `content_loc8` = NULL,`broadcast_text_id` = 30554 WHERE `entry` = -1599030;
UPDATE `script_texts` SET `content_loc8` = NULL,`broadcast_text_id` = 30555 WHERE `entry` = -1599031;
UPDATE `script_texts` SET `content_loc8` = 'Порог допустимой угрозы превышен. Астрономический архив отключен. Уровень безопасности повышен.' WHERE `entry` = -1599032;
UPDATE `script_texts` SET `content_loc8` = NULL,`broadcast_text_id` = 30556 WHERE `entry` = -1599033;
