INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000003430,'Your surge needle appears to have worked perfectly.  Surely Goramosh now has time to speak further of the proposed accord?',0,0,0,0,NULL),
(2000003431,'I was personally assured by Malygos that your commander would make the details of this accord his top priority. Now let me pass!',0,0,0,0,NULL),
(2000003432,'This is insufferable! I represent a nexus-prince, I am not to be made to wait! Goramosh should be waiting on me!',0,0,0,0,NULL),
(2000003433,'I have been waiting here for an eternity since our last meeting! When will Goramosh be done with his ritual?',0,0,0,0,NULL),
(2000003434,'If I have to wait much longer, I will be forced to reconsider the proposed accord between the Ethereum and the Blue Dragonflight!',0,0,0,0,NULL),
(2000003435,'Yes, yes, I am given to understand that the ley line has already been diverted. I will wait no longer... summon Goramosh this instant!',0,0,0,0,NULL),
(2000003436,'You are nothing but an underling! I will not wait a moment longer! Escort me to Goramosh immediately!',0,0,0,0,NULL),
(2000003437,'You will tell your commander that I will not wait a moment longer! Does he want this alliance or not?!',0,0,0,0,NULL),
(2000003438,'My apologies, ambassador. My orders were quite clear.',0,0,0,396,NULL),
(2000003439,'Goramosh is busy with a ritual right now.',0,0,0,396,NULL),
(2000003440,'My orders are that you must wait here.',0,0,0,396,NULL),
(2000003441,'The master will be with you momentarily.',0,0,0,0,NULL),
(2000003442,'You\'ll have to be patient.',0,0,0,0,NULL),
(2000003443,'It won\'t be long now.',0,0,0,0,NULL);
