INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000003465,'I\'ve lived here and kept this shrine with the blessing of the Alexstrasza for longer than I can remember.  In its darkest hour, I\'ve failed... I fell at the feet of that damned death knight...  Bloodbane.',0,0,0,0,NULL),
