INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000003576,'Kor\'kron, stand down!',16222,0,0,5,NULL),
(2000003577,'Jaina...',16223,0,0,1,NULL),
(2000003578,'Thrall, what has happened? The King is preparing for war...',16129,0,0,6,NULL),
(2000003579,'Jaina, what happened at the Wrath Gate. It was a betrayal from within...',16224,0,0,1,NULL),
(2000003580,'Lady Proudmoore, the Warchief speaks the truth. This subterfuge was set in motion by Varimathras and Grand Apothecary Putress. It was not the Horde\'s doing.',16315,0,0,1,NULL),
(2000003581,'As the combined Horde and Alliance forces began their assault upon the Wrath Gate, an uprising broke out in the Undercity. Varimathras and hordes of his demonic brethren attacked. Hundreds of my people were slain in the coup. I barely managed to escape with my life.',16316,0,0,1,NULL),
(2000003582,'The Horde has lost the Undercity.',16225,0,0,1,NULL),
(2000003583,'We now prepare to lay siege to the city and bring the perpetrators of this unforgivable crime to justice.',16226,0,0,1,NULL),
(2000003584,'If we are forced into a conflict, the Lich King will destroy our divided forces in Northrend.',16227,0,0,1,NULL),
(2000003585,'We will make this right, Jaina. Tell your king all that you have learned here.',16228,0,0,1,NULL),
(2000003586,'I will deliver this information to King Wrynn, Thrall, but...',16130,0,0,1,NULL),
(2000003587,'Bolvar was like a brother to him. In the King\'s absence, Bolvar kept the Alliance united. He found strength for our people in our darkest hours. He watched over Anduin, raising him as his own. ',16131,0,0,1,NULL),
(2000003588,'I fear that the rage will consume him, Thrall. I remain hopeful that reason will prevail, but we must prepare for the worst... for war.',16132,0,0,1,NULL),
(2000003589,'Farewell, Warchief. I pray that the next time we meet it will be as allies.',16133,0,0,1,NULL),
(2000003590,'Kor\'kron, prepare transport to the Undercity.',16229,0,0,1,NULL);
