INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000003736,'Steel yourselves, soldiers. $N has provided us with the final piece of this puzzle. The dread lich, Thel\'zan, will soon come out of hiding, only to be rendered powerless against us!',0,0,0,5,NULL),
(2000003737,'It is this phylactery - Thel\'zan\'s phylactery - that is the key to victory!',0,0,0,397,NULL),
(2000003738,'I was... once like you. Mortal. Soft. Only flesh and bone. WEAK AND TIMID!',0,1,0,0,NULL),
(2000003739,'LEGION, STEADY YOURSELVES!',0,0,0,0,NULL),
(2000003740,'Our lord, Arthas, saw in me the frailty of man.',0,1,0,1,NULL),
(2000003741,'\"Serve me in life and I will promise to rend the weakness from your soul. To erase it from existence!\" To be chosen... blessed by the Lich King in such a way.',0,1,0,1,NULL),
(2000003742,'So serve him I did... Were it not for me the glorious dread citadel of Naxxramas may never have returned to Northrend.',0,1,0,1,NULL),
(2000003743,'But enough talk... You have fought hard and come from the distant reaches of this world to face the Duskbringer. I shall not disappoint...\n',0,1,0,1,NULL),
(2000003744,'Just as our lord saved me, so too shall I save you!\n',0,1,0,1,NULL),
(2000003745,'THIS WILL BE THE LAST TIME YOU UTTER THAT NAME, PALADIN! Breathe your dying breath!',0,1,0,1,NULL),
(2000003746,'Rise, my minions! Your master commands it!',0,1,0,1,NULL),
(2000003747,'Honor, courage, compassion and justice! These were once the virtues you fought for as a cleric of the Argent Dawn, Thel\'zan.',0,1,0,22,NULL),
(2000003748,'You dedicated your whole life to fighting the Scourge. You saw what they did to our beloved Lordaeron. The Lich King took everything from you!',0,1,0,5,NULL),
(2000003749,'So how, then, did Father Inigo Montoy, bastion of virtue for the Argent Dawn, become the embodiment of that which he hated most?',0,0,0,5,NULL),
(2000003750,'Now, be free and join the battle! Let us end this together!',0,0,0,0,NULL),
(2000003751,'You have saved Wintergarde! With Thel\'zan\'s filth washed away, the 7th Legion will easily clean up the remaining Scourge. When you are done here, report to Angrathar!',0,0,0,1,NULL),
(2000003752,'It\'s the Highlord!',0,0,0,0,NULL);
