INSERT INTO dbscript_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000003881,'I\'ll admit, dwarf, I was pleasantly surprised to see your plan succeed.',0,0,0,0,NULL),
(2000003882,'We can\'t just let you return to your companions and we can\'t take you with us...',0,0,0,0,NULL),
(2000003883,'Wait a minute here! You promised to spare me if I helped you. I held up my end o\' the bargain!',0,0,0,5,NULL),
(2000003884,'Stay your blade, Chieftain. He may yet have value to us.',0,0,0,1,NULL),
(2000003885,'Remember the dwarven journal that was discovered? Well, this dwarf could be exactly what we need to track down Brann Bronzebeard and capture him.',0,0,0,1,NULL),
(2000003886,'By the Light, Brann\'s alive? I\'d never help you capture him!',0,0,0,1,NULL),
(2000003887,'You will if you wish to keep your head attached to your shoulders.',0,0,0,1,NULL),
(2000003888,'Does that satisfy you, Tormak?',0,0,0,6,NULL),
(2000003889,'He will live so long as he proves useful. I\'d be careful if I was you, dwarf.',0,0,0,273,NULL);
